	add(p, n) {

		const nx = n.x;
		const ny = n.y;
		const nz = n.z;

		const dot = n.dot(p);

		const ata = this.ata.elements;
		const atb = this.atb;

		ata[0] += nx * nx; ata[1] += nx * ny; ata[2] += nx * nz;
		ata[3] += ny * ny; ata[4] += ny * nz;
		ata[5] += nz * nz;

		atb.x += dot * nx;
		atb.y += dot * ny;
		atb.z += dot * nz;

