		this.atb.set(0, 0, 0);
		this.btb = 0;

		this.massPoint.set(0, 0, 0);
		this.numPoints = 0;

	}

	/**
	 * Clones this data.
	 */

	clone() {

		return new this.constructor().copy(this);

	}

